export default function LocationSection() {
  const [hovered, setHovered] = useState<string | null>(null);
  const [mouse, setMouse] = useState<{ x: number; y: number } | null>(null);
  const navigate = useNavigate(); // v6
  // const history = useHistory(); // v5

  // Obsługa kliknięcia w województwo
  const handleRegionClick = (regionName: string) => {
    // Przekierowanie na podstronę z wybranym województwem (np. /masazystki?region=Pomorskie)
    navigate(`/masazystki`, { state: { region: regionName } });
    // Jeśli chcesz query param zamiast state:
    // navigate(`/masazystki?region=${encodeURIComponent(regionName)}`);
    // history.push(`/masazystki?region=${encodeURIComponent(regionName)}`); // v5
  };

  // Obsługa kliknięcia w guzik "Zobacz wszystkie miasta"
  const handleAllCitiesClick = () => {
    navigate("/masazystki");
    // history.push("/masazystki"); // v5
  };

  return (
    <section className="w-full relative bg-white flex flex-col items-center justify-center pt-0 pb-20">
      <div className="w-full max-w-6xl mx-auto">
        {/* Wybierz miasto / województwo + mapa */}
        <div className="w-full flex flex-col md:flex-row justify-between gap-8">
          {/* Lewa kolumna */}
          <div className="w-full md:w-1/2 px-2 md:px-0">
            <h2 className="text-2xl md:text-3xl font-light text-gray-800 mb-2">
              <span className="font-bold">Wybierz</span> miasto lub województwo
            </h2>
            <div className="text-md text-gray-800 font-light mb-3">
              Wybierz lokalizację i znajdź masażystów oraz salony w swojej okolicy.
            </div>
            <ul className="text-gray-800 font-light text-base">
              {VOIVODESHIPS.map((v, i) => (
                <li
                  key={i}
                  className="flex items-center group cursor-pointer hover:underline"
                  onClick={() => handleRegionClick(v)}
                >
                  <span className="text-lg mr-1">•</span>
                  <span className="mr-2 font-medium">{v}</span>
                  <span className="text-gray-500 group-hover:text-gray-800 text-sm">›</span>
                </li>
              ))}
            </ul>
          </div>
          {/* Prawa kolumna */}
          <div className="w-full md:w-1/2 flex flex-col justify-center items-center mt-10 md:mt-0" style={{ position: "relative" }}>
            {/* Tooltip z nazwą województwa */}
            {hovered && mouse && (
              <div
                style={{
                  position: "absolute",
                  left: mouse.x + 10,
                  top: mouse.y - 26,
                  background: "#ecb742",
                  color: "#fff",
                  borderRadius: 6,
                  fontSize: "0.95rem",
                  padding: "4px 12px",
                  pointerEvents: "none",
                  zIndex: 50,
                  whiteSpace: "nowrap",
                  boxShadow: "0 2px 8px rgba(0,0,0,0.10)"
                }}
              >
                {hovered}
              </div>
            )}
            <svg
              viewBox="0 0 535 535" // Dopasuj do swojego SVG!
              className="max-w-xs md:max-w-md w-full h-auto"
              style={{ userSelect: "none" }}
              onMouseMove={e => {
                const rect = e.currentTarget.getBoundingClientRect();
                setMouse({
                  x: e.clientX - rect.left,
                  y: e.clientY - rect.top,
                });
              }}
              onMouseLeave={() => { setHovered(null); setMouse(null); }}
            >
              {paths.map((region) => (
                <path
                  key={region.name}
                  d={region.d}
                  fill={hovered === region.name ? "#ecb742" : "#e5e5e5"}
                  stroke="#fff"
                  strokeWidth={2}
                  onMouseEnter={() => setHovered(region.name)}
                  onMouseLeave={() => setHovered(null)}
                  onClick={() => handleRegionClick(region.name)}
                  style={{ cursor: "pointer", transition: "fill 0.2s" }}
                />
              ))}
            </svg>
          </div>
        </div>
      </div>
      {/* Przycisk na dole - zawsze idealnie wycentrowany, tuż przy dolnej krawędzi */}
      <div className="absolute left-0 right-0 flex justify-center pointer-events-none" style={{ bottom: '-1.5rem' }}>
        <button
          className="pointer-events-auto bg-[#ecb742] hover:bg-[#ffe1a1] transition-colors text-white font-medium px-6 py-2 rounded shadow"
          onClick={handleAllCitiesClick}
        >
          Zobacz wszystkie miasta
        </button>
      </div>
    </section>
  );
}